class Scene:
    def __init__(self, level: int, mx: int, my: int, map: list[list], raw=[]):
        self.level, self.mx, self.my, self.map = level, mx, my, map
        self.enemies_raw, self.enemies = raw, []

        self.attacks, self.explosions, self.triggers, self.text = [], [], [], []
        levels[level].append(self)

class Text:
    def __init__(self, level: int, mx: int, my: int, message: str, x, y, color='WHITE'):
        self.message, self.color = message, color
        self.x, self.y = 32 * x + 16, 32 * y + 16

        for scene in levels[level]:
            if scene.mx == mx and scene.my == my:
                scene.text.append(self)
                break


